INSERT INTO `feedback` (`id_feedback`, `user`, `email`, `name`, `text`, `evaluation`, `up`, `down`) VALUES
(8, 'Ирина', 'i@mail.ru', 'MCamp', 'дочери очень понравилось', 4, 12, 2),
(12, 'Алексей Анатольевич', 'mymail@mail.mail', 'Enjoy Camp. Домодедово', 'Сын сказал что все супер. В следующем году снова к вам!', 5, 4, 3),
(13, 'jopa', 'faf@mail.ru', 'Enjoy Camp. Домодедово', 'gavno', 2, 1, 11),
(14, 'Оксана', 'oksik@mail.ru', 'Живая легенда', 'Уже просит поехать на весеннюю смену, для меня этим все сказано. Были летом на Сталкере, осенью, на зимней новогодней смене.', 5, 1, 0),
(15, 'Татьяна', 'tanechka@mail.ru', 'Живая легенда', 'Что понравилось: Понравилось абсолютно всё. Дочь никогда не была в лагере, это был первый раз. Благодарю,что подарили моему ребёнку много эмоций и впечатлений Что не понравилось: Немного прохладно было в комнате, это был маленький минус О вожатых и персонале: Дочери все понравились О питании:Кормили вкусно\r\n\r\nТатьяна,\r\nмама ребенка 13 лет', 5, 1, 0),
(16, 'Любовь', 'lubasha@mail.ru', 'Живая легенда', 'Большой главный корпус. Большая территория, на которой особо делать нечего, разве что прогуливаться. Бассейн с сомнительной водой, для которого просят брать лекарства от поноса и рвоты.Ужасные старые номера, всё обшарпаное и разваливается. В общем старый совдеповский санаторий. Ребёнок был на сборах по каратэ в первый раз и ему понравилось,', 2, 0, 2),
(17, 'Анна Поливановская', 'ahuta@yandex.ru', 'Марабу. Шато Le Sallay', 'Спасибо огромное за прекрасный лагерь! Димке очень понравилось! Второй вечер в семье проводим свечку! Отличная традиция... =) Хотя в течение смены были заминки, но сейчас очень приятные воспоминаниям у ребёнка! Спасибо огромное за Ваши труды, новые знания и опыт для моего сына!', 5, 0, 0),
(18, 'Камила Туржова', 'kamikami@gmail.com', 'Марабу. Шато Le Sallay', 'Уважаемые организаторы! Я так вам всем благодарна за счастье своих детей:) Вернулся сын и без умолку рассказывает, какие вы крутые и весёлые и интересные и т.д. Надо, говорит, прямо сейчас записаться на следующий год! :))) Больше всего меня поражает в этом то, что вы умудряетесь каким-то хитрым образом поддерживать этот уровень восторга уже 4-ый год (если вести отсчёт от первой поездки моей старшей дочери). Пусть у вас все будет хорошо и дальше! Ну и счастья вам и радости! Везде и во всем.', 5, 0, 0),
(19, 'Olga Markova', 'olechka@hh.ru', 'Марабу. Шато Le Sallay', 'Спасибо команде \"Марабу\"за то, что делаете детей счастливыми.\r\nЗа любовь к математике (какое волшебство там творит Ян Яныч Раух я даже не могу себе представить. Чтобы мой ребенок увлеченно рассказывал про уроки математики??? Никогда б в такое не поверила))\r\nСпасибо за то, что \"Марабу\" приглашает лучших специалистов из разных областей. И не просто специалистов - людей,умеющих найти общий язык с каждым ребенком и заинтересовать! Митину астрономию Ваня ждал с прошлого лета!\r\nА про уникальные факты \"звездных войн\" мы слушали всю дорогу из аэропорта и ещё вечером за ужином.\r\nОтдельное спасибо вожатым!Такое разнообразие игр, активностей - вы суперпрофи) \"Свечку\" мой сын предлагает проводить теперь и дома)))\r\nЗдорово, что был ограниченный доступ к гаджетам - ребенок приехал со свежей головой и настоящими впечатлениями.\r\nСразу после аэропорта разбирали грамоты, открытки, \"верёвочки\" и уже грустили...\r\nСпасибо всем, дорогие марабушники! Вы - большие молодцы 💕 вы делаете большое и оч.важное дело!! Удачи вам во всех начинаниях ✨', 5, 0, 0),
(20, 'Наталья', 'krytaya@yandex.ru', 'Голубые ели', 'Более-менее программа ребенку понравилась и размещение вроде бы было неплохое.Но питание оцениваю на два балла, оно было вообще плохое. В лагере отдыхали сын и племянник, племянник присылал фотографии еды — она какая-то несовместимая, выглядит совсем не аппетитно. Сын, конечно, не жаловался.В лагере скучно им было, особенно первые полторы недели, их ничем не занимали. Сразу телефоны не давали, а потом, когда дали, стали звонить — а они ничего не делают. Мы были и в другом лагере,так там с детьми с первого дня занимались. А здесь большую часть времени ребята сами себя развлекали. Потом подружились с вожатыми, и стало веселее. В лагерь больше не поедут, им было скучно. ', 4, 0, 0),
(21, 'Елена', 'elena@mail.com', 'Голубые ели', 'В первые дни ребенку понравилось, а потом уже нет. По словам ребенка, еда была невкусной,постоянно ходил голодный.Неинтересная программа, \"ничего особенного\", как он сказал. Особо не гуляли, он как приехал бледный, так и вернулся, несмотря на отсутствие дождей. Видимо, ребенок солнца вообще не видел. А еще заболел простудой, но никакой медицинской помощи ему не оказали. Отзывы мы читали перед поездкой, вроде все хорошие. Но на деле как-то не особо оказалось...В этот лагерь он вряд ли уже захочет ехать.', 4, 0, 0),
(22, 'Олег', 'oleja@ya.ru', 'Голубые ели', 'Поначалу все было хорошо, пока не привезли детей из интерната, из-за этого ребенок собирался домой, были конфликты, дети там совсем другие,даже вожатые не могли с ними справиться. Потом их вывозили на Байкал и везде, поэтому дочь добыла с горем пополам до конца смены. Кроме этого все было прекрасно. Питание можно было бы улучшить: ребенок немного жаловалась, говорила, что давали гнилые фрукты. Размещение понравилось. В целом, вожатые хорошо отработали смену, на пятерочку. Следующим летом дочь хотела бы поехать еще в этот лагерь, если детей из интерната не будет.', 5, 0, 0),
(23, 'Алина', 'alinka@mail.ru', 'MCamp', 'Отлично!', 5, 1, 0);

